INSERT INTO `song` VALUES ('1842927464', 'Waiting For Love', 'Avicii', '[00:00.000]\n[00:00.000] 作词 : Avicii/Salem Al Fakir/Vincent Pontare/Simon Aldred/Martin Garrix\n[00:01.000] 作曲 : Avicii/Salem Al Fakir/Vincent Pontare/Simon Aldred/Martin Garrix\n[00:14.387]Where there\'s a will, there\'s a way, kind of beautiful\n[00:18.460]And every night has its day, so magical\n[00:22.036]And if there\'s love in this life, there\'s no obstacle\n[00:25.646]That can\'t be defeated\n[00:29.614]For every tyrant a tear for the vulnerable\n[00:33.262]In every lost soul the bones of a miracle\n[00:37.000]For every dreamer a dream we\'re unstoppable\n[00:40.634]With something to believe in\n[00:44.792]Monday left me broken\n[00:47.944]Tuesday I was through with hoping\n[00:51.560]Wednesday my empty arms were open\n[00:55.258]Thursday waiting for love, waiting for love\n[01:00.020]Thank the stars it\'s Friday\n[01:02.977]I\'m burning like a fire gone wild on Saturday\n[01:06.512]Guess I won\'t be coming to church on Sunday\n[01:10.105]I\'ll be waiting for love\n[01:12.892]Waiting for love to come around\n[02:07.055]We are one of a kind, irreplaceable\n[02:10.685]How did I get so blind and so cynical?\n[02:14.436]If there\'s love in this life we\'re unstoppable\n[02:18.127]No, we can\'t be defeated\n[02:22.534]Monday left me broken\n[02:25.496]Tuesday I was through with hopin\'\n[02:29.163]Wednesday my empty arms were open\n[02:32.854]Thursday waiting for love, waiting for love\n[02:37.488]Thank the stars it\'s Friday\n[02:40.262]I\'m burning like a fire gone wild on Saturday\n[02:44.002]Guess I won\'t be coming to church on Sunday\n[02:47.638]I\'ll be waiting for love\n[02:50.422]Waiting for love to come around\n', 'http://music.163.com/song/media/outer/url?id=1842927464.mp3', 'http://p2.music.126.net/A_vJ_J2jQeTEeJwGfaZiZg==/109951165955804995.jpg?param=300x300');
