INSERT INTO `song` VALUES ('1942311009', '可惜不是你', '刘大壮', '[00:00.000]\n[00:04.59]词：李焯雄\n[00:05.37]曲：曹轩宾\n[00:06.30]出品：网易飓风/猕桃隐约音乐\n[00:20.07]这一刻突然觉得好熟悉\n[00:27.30]像昨天今天同时在放映\n[00:34.77]我这句语气原来好像你\n[00:42.24]不就是我们爱过的证据\n[00:49.53]差一点骗了自己骗了你\n[00:57.27]爱与被爱不一定成正比\n[01:04.35]我知道被疼是一种运气\n[01:11.76]但我无法完全交出自己\n[01:18.87]努力为你改变\n[01:21.15]却变不了预留的伏线\n[01:26.28]以为在你身边那也算永远\n[01:33.63]仿佛还是昨天\n[01:35.94]可是昨天已非常遥远\n[01:41.07]但闭上我双眼我还看得见\n[01:48.81]可惜不是你\n[01:52.56]陪我到最后\n[01:56.22]曾一起走却走失那路口\n[02:03.63]感谢那是你\n[02:07.20]牵过我的手\n[02:11.04]还能感受那温柔\n[02:17.94]努力为你改变\n[02:20.22]却变不了预留的伏线\n[02:25.41]以为在你身边那也算永远\n[02:32.70]仿佛还是昨天\n[02:34.98]可是昨天已非常遥远\n[02:40.17]但闭上我双眼我还看得见\n[02:47.97]可惜不是你\n[02:51.72]陪我到最后\n[02:55.35]曾一起走却走失那路口\n[03:04.59]还能感受那温柔\n[03:07.62]编曲：卡其漠（罗洋）、王灏\n[03:08.22]吉他：大牛\n[03:08.58]混音：任天宇\n[03:09.90]和声：刘海丰\n[03:10.23]制作人：芮英杰\n[03:10.62]企划：刘冠伶\n[03:11.04]监制：胡圣羽\n[03:11.34]出品人：谢奇笛\n[03:11.70]营销推广：网易飓风\n[03:12.27]业务联系：jf399@ vip.163. com\n', 'http://music.163.com/song/media/outer/url?id=1942311009.mp3', 'http://p1.music.126.net/VbSnG_XFCFaNVRm_MhY9qw==/109951167350406105.jpg?param=300x300');
